      );
    ,
      if(%9 != null,
        playsound(%1,%2,%8,stopsound3Dwhenpossible(%1,%4));
      ,
        playsound(%1,%2,%8);
      );
    );
  ,
    if(%6 != null,
      playsound(%1,%2,%5,stopsound3Dwhenpossible(%1,%4));
    ,
      playsound(%1,%2,%5);
    );
  );

  ifnot(tour_soundson,
    set(tour_current_played_sound_paused, true);
    pausesound(%1);
  );
);

